mapper.select:
select
d.exam_time, d.exam_id, d.user_id, d.create_time as start_time , a.name as testName, d.exam_achievement as examAchievement, d.if_whether as ifWhether, c.name as testUserName, c.unit_name as unitName, c.department_name as departmentName from mp_examination a , ( SELECT max(f.create_time) as create_time , f.exam_id , f.user_id from mp_exam_achievement f inner join (SELECT max( exam_achievement) as exam_achievement , user_id,exam_id from mp_exam_achievement where dele_flag = '0' group by exam_id, user_id )g on f.user_id =g.user_id and f.exam_id =g.exam_id and f.exam_achievement =g.exam_achievement GROUP BY f.exam_id , f.user_id ) b, mp_user_authentication c, mp_exam_achievement d
where d.exam_id = a.id and b.user_id =d.user_id and b.exam_id =d.exam_id and b.create_time =d.create_time and c.id = d.user_id and c.dele_flag = '0' and a.dele_flag = '0' and d.dele_flag= '0'
and b.user_id = #{dto.userId}
and b.exam_id = #{dto.examId}
and d.create_time >= #{dto.dateStart} and d.create_time < #{dto.dateEnd}
and c.name like CONCAT("%",#{dto.testUserName},"%")
and c.unit_name like CONCAT("%",#{dto.unitName},"%")
and c.department_name like CONCAT("%",#{dto.departMentName},"%")
and d.if_whether = #{dto.ifWhether}

select
d.exam_time, d.exam_id, d.user_id, d.create_time as start_time , a.name as testName, d.exam_achievement as examAchievement, d.if_whether as ifWhether, c.name as testUserName, c.unit_name as unitName, c.department_name as departmentName from mp_examination a , ( SELECT max(f.create_time) as create_time , f.exam_id , f.user_id from mp_exam_achievement f inner join (SELECT max( exam_achievement) as exam_achievement , user_id,exam_id from mp_exam_achievement where dele_flag = '0' group by exam_id, user_id )g on f.user_id =g.user_id and f.exam_id =g.exam_id and f.exam_achievement =g.exam_achievement GROUP BY f.exam_id , f.user_id ) b, mp_user_authentication c, mp_exam_achievement d
where d.exam_id = a.id and b.user_id =d.user_id and b.exam_id =d.exam_id and b.create_time =d.create_time and c.id = d.user_id and c.dele_flag = '0' and a.dele_flag = '0' and d.dele_flag= '0'
and b.user_id = #{dto.userId}
and b.exam_id = #{dto.examId}
and d.create_time >= #{dto.dateStart} and d.create_time < #{dto.dateEnd}
and c.name like CONCAT("%",#{dto.testUserName},"%")
and c.unit_name like CONCAT("%",#{dto.unitName},"%")
and c.department_name like CONCAT("%",#{dto.departMentName},"%")
and d.if_whether = #{dto.ifWhether}
